INSERT INTO `modules` (`id`, `class`, `flags`, `data`, `created`) VALUES('178', 'ProcessAdminActions', '1', '{\"dbBackup\":\"automatic\",\"showActionCode\":null,\"core\":{\"ConvertFieldsToMultiLanguage\":{\"roles\":[38],\"title\":\"Convert Fields to Multi-Language\",\"description\":\"Converts fields to their matching multi-language fieldtype.\",\"notes\":\"Shows a list of potential fields that can be selected for conversion.\",\"icon\":\"language\",\"author\":\"Philipp Daun\",\"authorLinks\":{\"pwforum\":\"3989-dhinnisda%C3%ABl\",\"pwdirectory\":\"philippdaun\",\"github\":\"daun\"}},\"CopyContentToOtherField\":{\"roles\":[38],\"title\":\"Copy Content to Other Field\",\"description\":\"This action copies the content from one field to another field on all pages that use the selected template.\",\"notes\":\"This can be useful if you decide you need to split one field into two to allow different settings on different templates. It also makes it easy to move content from one field type to another one that is incompatible.\",\"icon\":\"copy\",\"author\":\"Adrian Jones\",\"authorLinks\":{\"pwforum\":\"985-adrian\",\"pwdirectory\":\"adrian-jones\",\"github\":\"adrianbj\"}},\"CopyFieldContentToOtherPage\":{\"roles\":[38],\"title\":\"Copy Field Content to Other Page\",\"description\":\"Copies the content from a field on one page to the same field on another page.\",\"notes\":\"This can be useful if you decide to restructure where certain content lives on the site.\",\"icon\":\"copy\",\"author\":\"Adrian Jones\",\"authorLinks\":{\"pwforum\":\"985-adrian\",\"pwdirectory\":\"adrian-jones\",\"github\":\"adrianbj\"}},\"CopyPageTableItemsToOtherPage\":{\"roles\":[38],\"title\":\"Copy or Move PageTable Items to Other Page\",\"description\":\"Add the items from a PageTable field on one page to the same field on another page and optionally remove them from the original page.\",\"notes\":\"If the field on the destination page already has items, you can choose to append, or overwrite.\",\"icon\":\"copy\",\"author\":\"Jozsef Juhasz\",\"authorLinks\":{\"pwforum\":\"2990-jozsef\"}},\"CopyRepeaterItemsToOtherPage\":{\"roles\":[38],\"title\":\"Copy or Move Repeater Items to Other Page\",\"description\":\"Add the items from a Repeater\\/RepeaterMatrix field on one page to the same field on another page and optionally remove them from the original page.\",\"notes\":\"If the field on the destination page already has items, you can choose to append, or overwrite.\",\"icon\":\"copy\",\"author\":\"Adrian Jones\",\"authorLinks\":{\"pwforum\":\"985-adrian\",\"pwdirectory\":\"adrian-jones\",\"github\":\"adrianbj\"}},\"CopyTableFieldRowsToOtherPage\":{\"roles\":[38],\"title\":\"Copy Table Field Rows to Other Page\",\"description\":\"Add the rows from a Table field on one page to the same field on another page.\",\"notes\":\"If the field on the destination page already has rows, you can choose to append, or overwrite.\",\"icon\":\"copy\",\"author\":\"Adrian Jones\",\"authorLinks\":{\"pwforum\":\"985-adrian\",\"pwdirectory\":\"adrian-jones\",\"github\":\"adrianbj\"}},\"CreateUsersBatcher\":{\"roles\":[38],\"title\":\"Create Users Batcher\",\"description\":\"Allows you to batch create users.\",\"notes\":\"Having the Email New User module installed and configured to generate a password automatically is recommended. It is also recommended that you install the Password Force Change module.\",\"icon\":\"user-plus\",\"author\":\"Adrian Jones\",\"authorLinks\":{\"pwforum\":\"985-adrian\",\"pwdirectory\":\"adrian-jones\",\"github\":\"adrianbj\"}},\"DeleteUnusedFields\":{\"roles\":[38],\"title\":\"Delete Unused Fields\",\"description\":\"Deletes fields that are not used by any templates.\",\"notes\":\"Shows a list of unused fields with checkboxes to select those to delete.\",\"icon\":\"minus-circle\",\"author\":\"Adrian Jones\",\"authorLinks\":{\"pwforum\":\"985-adrian\",\"pwdirectory\":\"adrian-jones\",\"github\":\"adrianbj\"}},\"DeleteUnusedTemplates\":{\"roles\":[38],\"title\":\"Delete Unused Templates\",\"description\":\"Deletes templates that are not used by any pages.\",\"notes\":\"Shows a list of unused templates with checkboxes to select those to delete.\",\"icon\":\"minus-circle\",\"author\":\"Adrian Jones\",\"authorLinks\":{\"pwforum\":\"985-adrian\",\"pwdirectory\":\"adrian-jones\",\"github\":\"adrianbj\"}},\"EmailBatcher\":{\"roles\":[38],\"title\":\"Email Batcher\",\"description\":\"Lets you email multiple addresses at once.\",\"notes\":\"You can select \\\"Pages\\\" or \\\"User Roles\\\" for determining the recipients.\",\"icon\":\"envelope\",\"author\":\"Adrian Jones\",\"authorLinks\":{\"pwforum\":\"985-adrian\",\"pwdirectory\":\"adrian-jones\",\"github\":\"adrianbj\"}},\"FieldSetOrSearchAndReplace\":{\"roles\":[38],\"title\":\"Field Set or Search and Replace\",\"description\":\"Set field values, or search and replace text in field values from a filtered selection of pages and fields.\",\"notes\":\"This can be very destructive - please be careful!\",\"icon\":\"search-plus\",\"author\":\"Adrian Jones\",\"authorLinks\":{\"pwforum\":\"985-adrian\",\"pwdirectory\":\"adrian-jones\",\"github\":\"adrianbj\"}},\"FtpFilesToPage\":{\"roles\":[38],\"title\":\"FTP Files to Page\",\"description\":\"Add files\\/images from a folder to a selected page.\",\"notes\":\"This is useful if you want to FTP files, rather than upload via the admin.\",\"icon\":\"upload\",\"author\":\"Adrian Jones\",\"authorLinks\":{\"pwforum\":\"985-adrian\",\"pwdirectory\":\"adrian-jones\",\"github\":\"adrianbj\"}},\"PageActiveLanguagesBatcher\":{\"roles\":[38],\"title\":\"Page Active Languages Batcher\",\"description\":\"Lets you enable or disable active status of multiple languages on multiple pages at once.\",\"notes\":\"\",\"icon\":\"language\",\"author\":\"Adrian Jones\",\"authorLinks\":{\"pwforum\":\"985-adrian\",\"pwdirectory\":\"adrian-jones\",\"github\":\"adrianbj\"}},\"PageManipulator\":{\"roles\":[38],\"title\":\"Page Manipulator\",\"description\":